function writeAltLink(oAltLink) {

	if (oAltLink) {
		var oHead = document.getElementsByTagName("head")[0];
		var oLink = document.createElement('link');
		oLink.rel = "alternate";
		oLink.type = "application/rss+xml";
		oLink.href = oAltLink.url;
		oLink.title = oAltLink.title;
		oHead.appendChild(oLink);	
	}
}

function toggleIndustrySolutions(element) {
	var bIsOpen = element.hasClassName('expanded')
	$$('div.yourindustrysolutions div.content').each(function(item) {
		if (item.visible()) {
			Effect.BlindUp(item, { duration: 0.2 });
		}
		//item.hide();
	});
	$$('div.yourindustry a.expanded').each(function(item) {
		item.removeClassName('expanded');
		item.addClassName('expand');
	});
	if (!bIsOpen) {
		var id = element.readAttribute("rel");
		Effect.BlindDown(id, { duration: 0.2, queue: 'end' });
		//$(id).show();
		element.removeClassName('expand');
		element.addClassName('expanded');
	}
}

