(function($, $$){

  //global functions
  var tabNavigation, productSlider, activeTabId = 'tab0';
  
  tabNavigation = function( $tabContainer, $$tabs ){
    if($tabContainer && $tabContainer != undefined && $tabContainer.id ){
      var setActiveTab, hideTabContents = function() {
	    $$("#tabContents > *").each(function(o){
		  if (activeTabId.indexOf(o.id) == -1) {

          	o.style.display = "none";
		  }
       });
	  }
	  
      setActiveTab = function( tab ){
        tab.siblings().each(function(o){
          o.className = "";
        });
        
        tab.className = "active";
      };
      
      
      hideTabContents();
      
      $$tabs.each(function(o){
        o.observe("click", function(e){
          e.preventDefault();
		  
          var sRel = this.readAttribute("rel");
		  activeTabId = "tab" + getTabIdByRel(sRel);
		  // activeTabId = targ.replace("sector", "tab");

		  var sUrl = window.location.href;
		  if (sUrl.indexOf("#") > -1) {
		  
		  	sUrl = sUrl.substring(0, sUrl.indexOf("#")); 
		  }
		  window.location.href = sUrl + "#" + sRel;
		  
          setActiveTab( this.up() );
          $(activeTabId).show();
          hideTabContents();
		  
        });
      });
      /* if ($$(".tabs li.active a")[0] != null) {
      	$$(".tabs li.active a")[0].simulate("click");
	  } */

    }else{
      return false;
    }
  };
  
  
  productSlider = function( $slideContainer,slides ){
    if($slideContainer && $slideContainer != undefined &&  $slideContainer.id ){
      var $controlNext, $controlPrev, slideWidth, $$slides, totalSlides, $slideList, counter;
      
      $controlNext = $("goRight");
      $controlPrev = $("goLeft");
      $$slides = $$('#imageSlider li');
      $slideList = $$('#imageSlider div.slider > ul')[0];
	  $slideListContainer = $$('#imageSlider div.slider')[0];
      
      counter = 0;
      
      slideWidth = $$slides[0].getDimensions().width;
      totalSlides = $$slides.length;
      
	  
      $slideList.style.width = slideWidth * totalSlides + "px";
      
      $controlPrev.setStyle({
          visibility: "hidden"
        });
      
      if ( (slideWidth * totalSlides) > $slideListContainer.getDimensions().width ) {
        $controlNext.setStyle({
          visibility: "visible"
        });
      }else{
        $controlNext.setStyle({
          visibility: "hidden"
        });
      }
      
      $controlNext.observe("click", function(e){
        
		++counter;

        $controlPrev.setStyle({
          visibility: "visible"
        });
         
        if ( counter + slides === totalSlides ){
          $controlNext.setStyle({
          visibility: "hidden"
        });
        }else{
          $controlNext.setStyle({
          visibility: "visible"
        });
        }
        
        new Effect.Move($slideList, { x: "-" + slideWidth , y: 0, queue: {position: "end", scope: "scope"} });
         Event.stop(e);

		return false;
      });
      
      $controlPrev.observe("click", function(e){
        --counter;
        
        $controlNext.style.visibility = "visible";
         
        if ( !counter ){
          $controlPrev.style.visibility = "hidden";
        }else{
          $controlPrev.style.visibility = "visible";
        }
        
        new Effect.Move($slideList, { x: slideWidth , y: 0, queue: {position: "end", scope: "scope"} });
       Event.stop(e); 
		return false;
      });
      
    }else{
      return false;
    }
  }
  
  //document ready
  Event.observe(window, 'load', function() { 
  	var iTab = 0;
	if (window.location.href.indexOf("#") > -1) {
		var sRel = window.location.href.substring(window.location.href.indexOf("#") + 1);
		iTab = getTabIdByRel(sRel);
	}
  	if ($("productTabs")) {
    	tabNavigation( $("productTabs"),$$(".tabs a") );
    	productSlider( $('imageSlider'),4 );
		if ($('tabLink' + iTab) != null) {
			setTimeout('$("tabLink" + iTab).simulate("click")', 1500);
		}
	}
  });
}($, $$));

function getTabIdByRel(sRel) {
	var aoLinks = document.getElementsByTagName("A");
	var oLink;
	for (var iLinkCount = 0; iLinkCount < aoLinks.length; ++iLinkCount) {
	
		oLink = aoLinks[iLinkCount];
		if (oLink.rel == sRel) {
		
			iTab = parseInt(oLink.id.replace("tabLink", ""), 10);		
		}
	}
	return iTab;
}

