public class BeanTypeCase{

	private int id;
	private String description;
	private int days;

	/**
	  * @return 
	* Obtiene el valor de la variable id
	 */
	public int getId() {
		return id;
	}

	/**
	 * @param integer
	* Asigna el valor de integer a  la variable id
	 */
	public void setId(int integer) {
		id = integer;
	}

	/**
	 * @return 
	* Obtiene el valor de la variable description
	 */
	public String getDescription() {
		return description;
	}

	/**
	  * @param string  
	* Asigna un valor a la variable description
	 */
	public void setDescription(String string) {
		description = string;
	}

	/**
	 * @return 
	* Obtiene el valor de la variable days
	 */
	public int getDays() {
		return days;
	}

	/**
	 * @param i
	* Asigna el valor de i a  la variable id
	 */
	public void setDays(int i) {
		days = i;
	}

}